import type { WorkflowPreviewLayout } from "./workflowPreviewLayout.js";
import type { ProjectIssueTypeQueryContext } from "./projectIssueTypeQueryContext.js";
import type { WorkflowPreviewScope } from "./workflowPreviewScope.js";
import type { WorkflowPreviewStatus } from "./workflowPreviewStatus.js";
import type { TransitionPreview } from "./transitionPreview.js";
import type { WorkflowDocumentVersion } from "./workflowDocumentVersion.js";
/** Details of a workflow. */
export interface WorkflowPreview {
    /** The description of the workflow. */
    description?: string;
    /** The ID of the workflow. */
    id?: string;
    loopedTransitionContainerLayout?: WorkflowPreviewLayout;
    /** The name of the workflow. */
    name?: string;
    /** The project and issue type context for this workflow query. */
    queryContext?: ProjectIssueTypeQueryContext[];
    scope?: WorkflowPreviewScope;
    startPointLayout?: WorkflowPreviewLayout;
    /** The statuses referenced in this workflow. */
    statuses?: WorkflowPreviewStatus[];
    /** The transitions of the workflow. */
    transitions?: TransitionPreview[];
    version?: WorkflowDocumentVersion;
}
